import React from "react";
import "./App.css";
import mondaySdk from "monday-sdk-js";
import "monday-ui-react-core/dist/main.css"
//Explore more Monday React Components here: https://style.monday.com/
// import AttentionBox from "monday-ui-react-core/dist/AttentionBox.js";
import Button from "monday-ui-react-core/dist/Button.js";
// import Icon from "monday-ui-react-core/dist/Icon.js";
import Bolt from "monday-ui-react-core/dist/icons/Bolt.js";
const monday = mondaySdk();

class App extends React.Component {
  constructor(props) {
    super(props);

    // Default state
    this.state = {
      settings: {},
      name: "",
    };
  }

  componentDidMount() {
    // TODO: set up event listeners
  }

  render() {
    return <div className="App">
      <div style={{
        display: "flex",
        justifyContent: "space-evenly",
        alignItems: "center",
        width: "100%"
      }}>
        <Button lefticon={Bolt} kind={Button.kinds.PRIMARY} size={Button.sizes.LARGE} ariaLabel="My primary IconButton" style="background:green" />
        <Button righticon={Bolt} kind={Button.kinds.PRIMARY} size={Button.sizes.LARGE} ariaLabel="My secondary IconButton" />
        <Button icon={Bolt} kind={Button.kinds.PRIMARY}  size={Button.sizes.LARGE} ariaLabel="My tertiary IconButton" />
      </div>
    </div>;
  }
}

export default App;
